package com.nettyexception.bungeeping;import com.nettyexception.bungeeping.commands.PingCommand;import net.md_5.bungee.api.plugin.Plugin;/** * Created by Sören Simons on 26.10.2018. * » All rights reserved! * » Code with JetbrainsIDE. * » Java Runtime Environment */public class BungeePing extends Plugin {    @Override    public void onEnable() {        this.initCommand();        this.getLogger().info("Plugin successfully activated!");        this.getLogger().info("Coded by NettyException");        this.getLogger().info(">> BungeePing");    }    @SuppressWarnings("unused")    private void initCommand() {        this.getProxy().getPluginManager().registerCommand(this, new PingCommand());    }    @Override    public void onDisable() {        this.getLogger().info("Plugin successfully deactivated!");        this.getLogger().info("Coded by NettyException");        this.getLogger().info(">> BungeePing");    }}